import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:tracenac/core/APIClass/APIClass.dart';import '../assets_model_class/assets_model_class.dart';class AssetsController extends GetxController{  RxBool isLoading = false.obs;  final APIClass _apiClass = APIClass();  AssetsModelClass? assetsModelClass;  Future<void> getAssetsApi(text)async{    isLoading(true);    assetsModelClass = await _apiClass.getTracenacAssetsApi(text);    if(assetsModelClass?.status == true){      isLoading(false);    }else{      Get.snackbar("Failed", "Something Wrong?", backgroundColor: Colors.white, colorText: Colors.black);    isLoading(false);    }  }}