import 'package:hand_signature/signature.dart';import 'package:tracenac/core/utils/app_color.dart';import 'package:flutter/material.dart';class MultiCity extends StatefulWidget {  const MultiCity({super.key});  @override  State<MultiCity> createState() => _MultiCityState();}class _MultiCityState extends State<MultiCity> {  TextEditingController dateController = TextEditingController();  HandSignatureControl control = HandSignatureControl(    threshold: 0.01,    smoothRatio: 0.65,    velocityRange: 2.0,  );  // Function to show calendar  Future<void> _pickDate() async {    DateTime? picked = await showDatePicker(      context: context,      initialDate: DateTime.now(), // default date      firstDate: DateTime(2000),      lastDate: DateTime.now(),    );    if (picked != null) {      setState(() {        dateController.text = "${picked.year} - ${picked.month} - ${picked.day}";      });    }  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: Padding(        padding: const EdgeInsets.all(16.0),        child: SingleChildScrollView(            child: Column(          children: [            Row(              mainAxisSize: MainAxisSize.min,              children: [                Expanded(                  child: TextFormField(                    decoration: InputDecoration(                      hintText: "From",                      border: OutlineInputBorder(                        borderRadius: BorderRadius.circular(15),                      ),                    ),                  ),                ),                SizedBox(width: 10), // Add spacing between fields                Expanded(                  child: TextFormField(                    decoration: InputDecoration(                      hintText: "To",                      border: OutlineInputBorder(                        borderRadius: BorderRadius.circular(15),                      ),                    ),                  ),                ),              ],            ),            SizedBox(height: 10),            TextFormField(              controller: dateController,              readOnly: true,              onTap: (){                _pickDate();              },              decoration: InputDecoration(                hintText: "Select Date",                prefixIcon: Icon(Icons.calendar_month, color: AppColor.buttonColor,),                border: OutlineInputBorder(                  borderRadius: BorderRadius.circular(15),                ),              ),            ),            SizedBox(height: 10),            TextFormField(              minLines: 3,              maxLines: null,              decoration: InputDecoration(                hintText: "Address",                border: OutlineInputBorder(                  borderRadius: BorderRadius.circular(15),                ),              ),            ),            SizedBox(height: 10,),            Center(              child: AspectRatio(                aspectRatio: 2,                child: Stack(                  children: <Widget>[                    ClipRRect(                      borderRadius: BorderRadius.circular(13),                      child: Container(                        constraints: BoxConstraints.expand(),                        color: Colors.grey.shade300,                        child: HandSignature(                          control: control,                          type: SignatureDrawType.shape,                        ),                      ),                    ),                    CustomPaint(                      painter: DebugSignaturePainterCP(                        control: control,                        cp: false,                        cpStart: false,                        cpEnd: false,                      ),                    ),                  ],                ),              ),            ),            SizedBox(height: 30,),            /// create history button            ElevatedButton(              style: ElevatedButton.styleFrom(                shape: RoundedRectangleBorder(                  borderRadius:                  BorderRadius.circular(12),                ),                fixedSize: Size(                  MediaQuery.of(                    context,                  ).size.width,                  50,                ),              ),              onPressed: () {},              child: Text(                "Create History "                    "Entry",              ),            ),          ],        )),      ),    );  }}