import 'package:dotted_decoration/dotted_decoration.dart';import 'package:flutter/material.dart';class ProductDetails extends StatefulWidget {  const ProductDetails({super.key});  @override  State<ProductDetails> createState() => _ProductDetailsState();}class _ProductDetailsState extends State<ProductDetails> {  String productType = "Type A";  @override  Widget build(BuildContext context) {    return Scaffold(      body: Padding(        padding: const EdgeInsets.all(0.0),        child: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            TextFormField(              decoration: InputDecoration(                hintText: "Product Name",                border: OutlineInputBorder(                  borderRadius: BorderRadius.circular(15),                ),              ),            ),            SizedBox(height: 10),            DropdownButtonFormField(              value: productType,              decoration: InputDecoration(                hintText: "--Product Type--",                border: OutlineInputBorder(                  borderRadius: BorderRadius.circular(15),                ),              ),              items: [                DropdownMenuItem(child: Text("Type A"), value: "Type A"),                DropdownMenuItem(child: Text("Type B"), value: "Type B"),                DropdownMenuItem(child: Text("Type C"), value: "Type C"),              ],              onChanged: (value) {                setState(() {                  productType = value!;                });              },            ),            SizedBox(height: 30),            Text("Product Image Upload "),            SizedBox(height: 10),            Container(              height: 100,              width: 100,              decoration: DottedDecoration(shape: Shape.box),              child: IconButton(onPressed: () {}, icon: Icon(Icons.add)),            ),          ],        ),      ),    );  }}