import 'package:flutter/material.dart';import 'package:tracenac/core/utils/app_color.dart';import '../../tools/read_nfc/read_nfc.dart';import '../../tools/record_audio/record_audio.dart';import '../../tools/scanner/screen/scan_qr_page.dart';import '../../tools/upload_file/upload_file_screen/upload_file_screen.dart';import '../../tools/upload_video/upload_video.dart';class ThirdBottomScreen extends StatefulWidget {  const ThirdBottomScreen({super.key});  @override  State<ThirdBottomScreen> createState() => _ThirdBottomScreenState();}class _ThirdBottomScreenState extends State<ThirdBottomScreen> {  List<String> component = [    "Scan QR Code",    "Scan BarCode",    "Read NFC",    "Record Audio",    "Upload Image",    "Upload Video",  ];  Map<String, String> componentIcons = {    "Scan QR Code": "assets/images/qrCode1.png",    "Scan BarCode": "assets/images/barCode.png",    "Read NFC": "assets/images/nfc.png",    "Record Audio": "assets/images/recordAudio.png",    "Upload Image": "assets/images/uploadImage1.png",    "Upload Video": "assets/images/uploadVideo.png",  };  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.white,      appBar: AppBar(        backgroundColor: AppColor.appBarColor,        title: Text("Tools", style: TextStyle(fontWeight: FontWeight.bold,            fontFamily: "Proxima", fontSize: 21),),        automaticallyImplyLeading: false,      ),      body: Padding(        padding: const EdgeInsets.symmetric(vertical: 8.0, horizontal: 21),        child: Column(          children: [            Expanded(              child: GridView.builder(                itemCount: component.length,                gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(                  crossAxisCount: 2,                  childAspectRatio: 0.9,                ),                itemBuilder: (context, index) {                  String title = component[index];                  String imageIcon = componentIcons[title] ?? "assets/images/scanner.png"; // default icon if not found                  return InkWell(                    onTap: () {                      if (title == "Scan QR Code") {                        Navigator.of(context).push(MaterialPageRoute(builder: (context) => QrScannerPage()));                        // Navigator.of(context).push(MaterialPageRoute(builder: (context) => QRScannerScreen()));                      }                      if (title == "Scan BarCode") {                        Navigator.of(context).push(MaterialPageRoute(builder: (context) => QrScannerPage()));                      }                      if (title == "Read NFC") {                        Navigator.of(context).push(MaterialPageRoute(builder: (context) => ReadNfc()));                      }                      if(title == "Record Audio"){                        Navigator.of(context).push(MaterialPageRoute(builder: (context) => RecordAudio()));                      }                      if (title == "Upload Image") {                        Navigator.of(context).push(MaterialPageRoute(builder: (context) => UploadFileScreen()));                      }                      if (title == "Upload Video") {                        Navigator.of(context).push(MaterialPageRoute(builder: (context) => UploadVideo()));                      }                    },                    child: Container(                      height: 100,                      margin: EdgeInsets.all(8.0),                      padding: EdgeInsets.all(8.0),                      width: double.infinity,                      decoration: BoxDecoration(                        borderRadius: BorderRadius.circular(12),                        border: Border.all(color: Colors.grey.shade500),                      ),                      child: Column(                        mainAxisAlignment: MainAxisAlignment.center,                        crossAxisAlignment: CrossAxisAlignment.center,                        children: [                          Image.asset(imageIcon,scale: 3, height: 40, fit:                          BoxFit.cover ,                            color: AppColor.appBarColor,),                          SizedBox(height: 10),                          Text(                            title,                            maxLines: 3,                            overflow: TextOverflow.ellipsis,                            style: TextStyle(                              fontFamily: "Proxima",                              fontSize: 14,                            ),                            textAlign: TextAlign.center,                          ),                        ],                      ),                    ),                  );                },              ),            ),          ],        ),      ),    );  }}