import 'dart:developer';import 'package:get/get.dart';import 'package:tracenac/core/APIClass/APIClass.dart';import '../data/menu_model/menu_model_class.dart';class DynamicMenuController extends GetxController{  RxBool isLoading = false.obs;  final APIClass _apiClass = APIClass();  List<Menu> menuList = [];  GetMenuModelClass? getMenuModelClass;  @override  void onInit() {    super.onInit();    dynamicMenuApi();  }  Future<void> dynamicMenuApi()async{    log("Api call dynamicMenuApi==> ");    isLoading(true);    getMenuModelClass = await _apiClass.getDynamicMenuApi();    if(getMenuModelClass?.status == true){      menuList = getMenuModelClass?.msg?.menu ?? [];      isLoading(false);    }else{      isLoading(false);    }  }}