import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:get/get_core/src/get_main.dart';import 'package:tracenac/features/partner/screen/partner_profile_screen.dart';import '../controller/partner_controller.dart';class PartnerScreen extends StatefulWidget {  const PartnerScreen({super.key});  @override  State<PartnerScreen> createState() => _PartnerScreenState();}class _PartnerScreenState extends State<PartnerScreen> {  final PartnerController partnerController = Get.put(PartnerController());  @override  void initState() {    // TODO: implement initState    super.initState();    partnerController.getTracenacPartener('');  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(title: Text("Partner")),      body: Obx(() {        if (partnerController.isLoading.value) {          return Center(child: CircularProgressIndicator());        }        return Padding(          padding: const EdgeInsets.all(8.0),          child: Column(            children: [              SizedBox(height: MediaQuery.of(context).padding.top),              ListView.builder(                itemCount: partnerController.partnerModelClass?.msg?.length ?? 0,                shrinkWrap: true,                physics: NeverScrollableScrollPhysics(),                itemBuilder: (context, index) {                  final data = partnerController.partnerModelClass?.msg?[index];                  return InkWell(                    onTap: (){                      Get.to(()=> PartnerProfileScreen(                        partnerId: data?.partnerId ?? "",                        partnerName: data?.partnerName ?? "",                        partnerEmail: data?.partnerUserEmailId ?? "",                      ));                    },                      child: Column(                    crossAxisAlignment: CrossAxisAlignment.start,                    children: [                      Row(                        children: [                          Container(                            height: 50,                            width: 50,                            decoration: BoxDecoration(                                color: Colors.blue,                                borderRadius: BorderRadius.circular(6)                            ),                            child: Center(child: Text("${data?.partnerName?[0]}")),                          ),                          SizedBox(width: 10,),                          Column(                            crossAxisAlignment: CrossAxisAlignment.start,                            children: [                              Text(" ${data?.partnerName}",                                style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16),                              ),                              Text(" ${data?.partnerUserEmailId}"),                            ],                          ),                        ],                      ),                      // Row(                      //   mainAxisAlignment: MainAxisAlignment.spaceBetween,                      //   children: [                      //       Text("company: ${data?.company}"),                      //     Text("phone: ${data?.partnerAppUserPhoneNo}"),                      //                      //   ],                      // ),                      Divider(thickness: 1,)                    ],                  ));                },              ),            ],          ),        );      }),    );  }}