import 'dart:developer';import 'package:audioplayers/audioplayers.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:tracenac/core/APIClass/APIClass.dart';import '../model_class/check_uni_code_model_class.dart';import '../model_class/unicode_details_model_class.dart';class ScannerController extends GetxController {  RxBool isLoadaing = false.obs;  final APIClass _apiClass = APIClass();  CheckUniCodeDetailsModelClass? checkUniCodeDetailsModelClass;  UnicodeDetailsModelClass? unicodeDetailsModelClass;  UniqueCodeData? uniqueCodeData;  ProductData? productData;  Future<void> checkUniCodeApi(uCode)async{    try{      isLoadaing(true);      checkUniCodeDetailsModelClass = await _apiClass.checkUnicodeApi(uCode);      log("checkUniCodeDetailsModelClass report ==> ""${checkUniCodeDetailsModelClass?.report ?? ""}");      await checkUniCodeDetailsApi(uCode);      isLoadaing(false);    }catch (e){      isLoadaing(false);    }finally{      isLoadaing(false);    }  }  Future<void> checkUniCodeDetailsApi(uCode) async {    try{      isLoadaing(true);      unicodeDetailsModelClass = await _apiClass.unicodeApi(uCode);      if (unicodeDetailsModelClass?.status == true) {        isLoadaing(false);        uniqueCodeData = unicodeDetailsModelClass?.msg?.uniqueCodeData;        productData = unicodeDetailsModelClass?.msg?.productData;      } else {        isLoadaing(false);        uniqueCodeData = null;        productData = null;        Get.snackbar(          "${unicodeDetailsModelClass?.status}",          "UniqueCode not found",          backgroundColor: Colors.white,          colorText: Colors.black,        );      }    }catch (e){      isLoadaing(false);    }finally{      isLoadaing(false);    }  }}