import 'package:flutter/material.dart';import 'package:get/get.dart';import '../controller/tracenac_get_user_conroller.dart';class TracenacGetUserScreen extends StatefulWidget {  const TracenacGetUserScreen({super.key});  @override  State<TracenacGetUserScreen> createState() => _TracenacGetUserScreenState();}class _TracenacGetUserScreenState extends State<TracenacGetUserScreen> {  final TracenacGetUserConroller conroller = Get.put(TracenacGetUserConroller());  @override  void initState() {    // TODO: implement initState    super.initState();    conroller.getUserTracenac("");  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(title: Text("Tracenac User")),      body: Obx(() {        if (conroller.isLoading.value) {          return Center(child: CircularProgressIndicator());        }        return Padding(          padding: const EdgeInsets.all(8.0),          child: Column(          children: [            SizedBox(height: MediaQuery.of(context).padding.top),            ListView.builder(              itemCount: conroller.tracenacUserModelClass?.msg?.length ?? 0,              shrinkWrap: true,              physics: NeverScrollableScrollPhysics(),              itemBuilder: (context, index) {                return Column(                  crossAxisAlignment: CrossAxisAlignment.start,                  children: [                    Row(                      children: [                        Container(                          height: 50,                          width: 50,                          decoration: BoxDecoration(                            color: Colors.blue,                            borderRadius: BorderRadius.circular(6)                          ),                          child: Center(child: Text("${conroller.tracenacUserModelClass?.msg?[index].name?[0]}")),                        ),                      SizedBox(width: 10,),                      Column(                        crossAxisAlignment: CrossAxisAlignment.start,                        children: [                          Text(" ${conroller.tracenacUserModelClass?.msg?[index].name}",                            style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16),                          ),                          Text(" ${conroller.tracenacUserModelClass?.msg?[index].email}"),                        ],                      ),                      ],                    ),                    SizedBox(height: 10,),                  ],                );              },            ),          ],        ),        );      }),    );  }}