import 'package:flutter/material.dart';import 'package:tracenac/features/onboarding/onboarding_Screen.dart';import '../features/create_report/create_report_screen/create_report_screen.dart';import '../features/dashboard/ui/dashboard_screen.dart';import '../features/login/login_screen/login_screen.dart';import '../features/menu/menu_screen/menu_screen.dart';import '../features/splash/splash_screen/splash_screen.dart';class AppRoutes {  static const splashScreen = '/splash_screen';  static const loginScreen = '/login';  static const onboardingScreen = '/onboardingScreen';  static const menuScreen = '/menu_screen';  static const dashBoardScreen = '/dash_screen';  static const createReportScreen = '/create_report_screen';  static Route<dynamic> generateRoute(RouteSettings settings) {    switch (settings.name) {      case splashScreen:        return MaterialPageRoute(builder: (context) => SplashScreen());      case loginScreen:        return MaterialPageRoute(builder: (context) => const LoginScreen());      case dashBoardScreen:        return MaterialPageRoute(builder: (context) => DashboardScreen());      case onboardingScreen:        return MaterialPageRoute(builder: (context) => OnboardingScreen());      case createReportScreen:        return MaterialPageRoute(builder: (context) => CreateReportScreen());      default:        return MaterialPageRoute(          builder: (context) {            return const Scaffold(              body: Center(child: Text("No routes generated")),            );          },        );    }  }}